frappe.ui.form.on("Purchase Receipt Item", "qty", function(frm, cdt, cdn) {
    frm.doc.total_cases = 0;
    var item = locals[cdt][cdn];
    var total_pcs = 0;
    var net_weight = 0;
    var total_cases = 0;
    var c_factor = 0;
    $.each(frm.doc["items"] || [], function(i, item) {
    if(item.uom == "Case"){
    total_cases += item.qty;
    }
    if(item.uom == "Nos"){
    var c_factor = 0;
    frappe.call({
    method: "erpnext.stock.get_item_details.get_conversion_factor",
    args: {
    item_code: item.item_code,
    uom: "Case"
    },
    callback: function(r) {
    c_factor += (item.qty / r.message.conversion_factor);
    frm.doc.total_cases += c_factor
    console.log(c_factor);
     }
    });
    }

    total_pcs += (item.conversion_factor * item.qty);
    net_weight += (item.net_weight * item.qty);

    });
    total_cases += c_factor;
    console.log(total_cases);
    frm.doc.total_pcs = total_pcs;
    frm.doc.total_net_weight = net_weight;
    frm.doc.total_cases += total_cases;
    frm.refresh_field("total_pcs");
    frm.refresh_field("total_net_weight");
    frm.refresh_field("total_cases");
});

