frappe.ui.form.on("Sales Invoice", "pos_item", function(frm, cdt, cdn) { 

var added = false;

		// find row with item if exists
		$.each(frm.doc["items"] || [], function(i, d) {
			if(d.item_code===frm.doc.pos_item) {
				frappe.model.set_value(d.doctype, d.name, 'qty', d.qty + 1);
				added = true;
				return false;
			}
		});

		if(!added) {
			var d = frm.fields_dict.items.grid.add_new_row();
			frappe.model.set_value(d.doctype, d.name, 'item_code', frm.doc.pos_item);

			// after item fetch
			frappe.after_ajax(function() {
				setTimeout(function() {
					frappe.model.set_value(d.doctype, d.name, 'qty', 1);

				}, 100);
			});
		}
         frm.doc.pos_item="";
         frm.refresh_field("items");

});


